import React, {useState} from "react";
// import ImagenesItem from "./ImagenesItem";
import "animate.css";
import styles from "./ImagenesItem.module.css";
import ImagenesBody from "../Helper/ImagenesBody";
import PhotoModal from "./PhotoModal";


const Imagenes = (props) => {



    const [showModal, setShowModal] = useState(false);

    const showModalHandler = () => {

        setShowModal(true)
    };

    const hideModalHandler = () => {
        setShowModal(false)
    };


    return (
        <React.Fragment>
            {showModal && <PhotoModal onClose={hideModalHandler}></PhotoModal>}
            <div  className={`${styles.divcont}`}>
        {
            ImagenesBody.map(
                (imgSrc, index) => 
                (<img className={` ${styles.imgbody}`}
                 src={imgSrc.src} 
                 key={index} 
                 onClick={props.onShowModal}
                 alt='Imagenes Suca'/>
                ))

        }

    </div>
        </React.Fragment>
    )
};

export default Imagenes;


