const Imagenes = () => {

    state = { isOpen: false };

    handleShowDialog = () => {
                this.setState({ isOpen: !this.state.isOpen });
              };

    return (

            <div  className={`${styles.divcont}`}>
                {
                    ImagenesBody.map(
                        (imgSrc, index) => 
                        (<img className={` ${styles.imgbody}`}
                         src={imgSrc.src} 
                         key={index} 
                         onClick={this.handleShowDialog}
                         alt='Imagenes Suca'
                         {...this.state.isOpen && (
                            <dialog
                              className="dialog"
                              style={{ position: "absolute" }}
                              open
                              onClick={this.handleShowDialog}
                            >
                              <img
                                className="image"
                                src={ImagenesBody[0].src}
                                onClick={this.handleShowDialog}

                              />
                            </dialog>
                          )}
                         />

