import ImagenesBody from "../Helper/ImagenesBody";
import styles from "./Imagenes.module.css";
import "animate.css";




const Imagenes = () => {

    let animado = document.querySelectorAll('.animacion');
    const mostrarScroll = () => {
        let scrollTop = document.documentElement.scrollTop;
        for (let i=0; i < animado.length; i++) {
            let alturaAnimado = animado[i].offsetTop;
            if(alturaAnimado - 500 < scrollTop) {
                animado[i].style.opacity = 1;
            }
        }
    }

    window.addEventListener('scroll', mostrarScroll);



    return (

            <div className={`${styles.divcont}  ${styles.animate__animated} ${styles.animate__fadeInDown}`}>
                {
                    ImagenesBody.map((imgSrc, index) => (<img className={`${styles.animacion} ${styles.imgbody}`} src={imgSrc.src} key={index} alt='Imagenes Suca'/>))
                }
            </div>

    )
};

export default Imagenes;
