import ImagenesBody from "../Helper/ImagenesBody";
import styles from "./Imagenes.module.css";


const Imagenes = () => {
    let animado = document.querySelectorAll('.animacion');

const mostrarScroll = () => {
    let scrollTop = document.documentElement.scrollTop;
    for (i=0; i < animado.length; i++) {
        let alturaAnimado = animado[i].offsetTop;
        if(alturaAnimado < scrollTop) {
            animado[i].style.opacity = 1;
        }
    }
}

window.addEventListener('scroll', mostrarScroll);
    return (
        <div className={styles.divcont}>
            {
                ImagenesBody.map((imgSrc, index) => (<img className={`${styles.imgbody} ${styles.animacion}`} src={imgSrc[i].src} key={index} alt='Imagenes Suca'/>))
            }
        </div>
    )
};

export default Imagenes;
